mapper.select:
SELECT aa.index_id, aa.index_name,
COALESCE ( MAX ( CASE WHEN data_time = #{item.dataTime} THEN "value" END ), null ) AS ${item.value},
aa.time_type, aa.order_num orderNum FROM ( SELECT ni.index_id, ei."name" AS "index_name", di."value", di.data_time, di.time_type, mn.order_num FROM model_node mn LEFT JOIN node_index ni ON ni.node_id = mn.node_id LEFT JOIN energy_index ei ON ei.index_id = ni.index_id LEFT JOIN data_item di ON ni.index_id = di.index_id WHERE mn.parent_id = #{nodeId} AND di.data_time >= #{beginTime} AND di.data_time <= #{endTime} AND di.time_type = #{timeType}
and ei.energy_id=#{indexStorageId}
) aa GROUP BY aa.index_id, aa.index_name, aa.time_type, aa.order_num ORDER BY aa.order_num ASC
