mapper.select:
SELECT aa.index_id, aa.index_name,
MAX ( CASE WHEN data_time = #{item.dataTime} THEN "value" END ) AS ${item.value},
aa.time_type FROM ( SELECT ci.index_id, ci.NAME AS "index_name", di."value", di.data_time, di.time_type FROM data_item di LEFT JOIN energy_index ci ON di.index_id = ci.index_id LEFT JOIN facility_archives f ON f."id"=ci.equipment WHERE di.data_time >= #{beginTime} AND di.data_time < #{endTime} AND di.index_id IN #{indexId} AND di.time_type = #{timeType}
and ci.equipment=#{indexStorageId}
) aa GROUP BY aa.index_id,aa.index_name,aa.time_type
